let frameNumber = 0,
    playBackConst = 500,
    setHeight = document.getElementById("set-height"),
    vid = document.getElementById("v0");

function HeightTweaks() {
    setHeight.style.height = Math.floor(vid.duration) * playBackConst + "px"
}

function scrollPlay() {
    let frameNumber = window.pageYOffset / playBackConst;
    vid.currentTime = frameNumber, window.requestAnimationFrame(scrollPlay)
}
vid.addEventListener("loadedmetadata", HeightTweaks), window.requestAnimationFrame(scrollPlay);